<?php  $boundary = 6;  list($links, $lastpage) = px_get_page_links($vars['search_results']['results']['total_results'], $vars['offset'], $vars['products_per_page'], $boundary);?>      <ol id="pagination"><?php if(count($links)): ?><?php if($vars['offset'] > $boundary + 1): ?>        <li class="first"><?php echo px_create_link('First', $vars['baseurl'].'&amp;offset=1', null, false, false); ?></li><?php endif; ?><?php foreach($links AS $id => $offset): ?>        <li><?php echo ($offset == $vars['offset']) ? $offset : px_create_link($offset, $vars['baseurl'].'&amp;offset='.$offset, null, false, false); ?></li><?php endforeach ?><?php if($vars['offset'] < ($lastpage - $boundary)): ?>        <li class="last"><?php echo px_create_link('Last', $vars['baseurl'].'&amp;offset='.$lastpage, null, false, false); ?></li><?php endif; ?><?php endif; ?>      </ol>